import React, { Component } from "react";
import monty from "./images/Monty_hall.png";
import "./App.css";
import { createMuiTheme } from "@material-ui/core/styles";
import { ThemeProvider } from "@material-ui/styles";
import { Button } from "@material-ui/core";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import TextField from "@material-ui/core/TextField";
import Checkbox from "@material-ui/core/Checkbox";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";

import Results from "./components/Results";

const theme = createMuiTheme({
    palette: {
        primary: {
            main: "#4F4F4F",
        },
        secondary: {
            main: "#CE8147",
        },
    },
});

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            apiResponse: null,
            numberOfSimulations: 1,
            numberInputError: false,
            switchDoors: false,
        };
    }

    callAPI() {
        fetch("http://localhost:9000/testAPI")
            .then((res) => res.text())
            .then((res) => this.setState({ apiResponse: res }))
            .catch((err) => err);
    }

    componentDidMount() {
        this.callAPI();
    }

    checkSimulationInput(event) {
      const newValue = event.target.value;
      if (isNaN(newValue)) {
        this.setState({
          numberInputError: true,
        });
      } else {
        this.setState({
          numberOfSimulations: parseInt(newValue),
          numberInputError: false,
        });
      }
    }

    runSimulations() {
      fetch("http://localhost:9000/results", {
        method: 'post',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json; charset=utf-8',
        },
        body: JSON.stringify({
          numberOfSimulations: this.state.numberOfSimulations,
          switchDoors: this.state.switchDoors
        })
      })
      .then(res => res.json())
      .then(results => this.setState({ apiResponse: results }))
      .catch((err) => err);
    }

    render() {
        return (
            <ThemeProvider theme={theme}>
                <div className='App'>
                    <AppBar position='sticky' color='primary'>
                        <Toolbar>
                            <Typography variant='h6'>
                                Monty Hall Simulator
                            </Typography>
                        </Toolbar>
                    </AppBar>
                    <div className='images'>
                        <img src={monty} alt='Monty Hall' />
                    </div>
                    <Typography>
                        Define the number of simulations to run and whether to
                        switch doors after having them opened the first time.
                    </Typography>
                    <div>
                        <FormControlLabel
                            control={
                                <Checkbox
                                    checked={this.state.switchDoors}
                                    onChange={() =>
                                        this.setState({
                                            switchDoors: !this.state
                                                .switchDoors,
                                        })
                                    }
                                    name='switchDoorsCheck'
                                />
                            }
                            label='Switch doors'
                        />
                        <TextField
                            error={this.state.numberInputError}
                            id='number-input'
                            label='Number of simulations'
                            defaultValue={this.state.numberOfSimulations}
                            helperText={this.state.numberInputError ? 'Incorrect entry.' : null}
                            onChange={e => this.checkSimulationInput(e)}
                        />
                    </div>
                    <Button variant='contained' color='secondary' disabled={this.state.numberInputError} onClick={() => this.runSimulations()}>
                        Run simulations
                    </Button>
                    <Results />
                    <div>Table with simulations</div>
                </div>
            </ThemeProvider>
        );
    }
}

export default App;
