import React, { Component } from 'react';
import monty from './images/Monty_hall.png';
import './App.css';

import { createMuiTheme } from '@material-ui/core/styles';
import { ThemeProvider } from '@material-ui/styles';
import { Button } from "@material-ui/core";
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';

const theme = createMuiTheme({
  palette: {
    primary: {
      main: "#4F4F4F",
    },
    secondary: {
      main: "#CE8147",
    },
  }
})

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      apiResponse: null,
      numberOfSimulations: null,
      switchDoors: false,
    };
  }

  callAPI() {
    fetch("http://localhost:9000/testAPI")
      .then(res => res.text())
      .then(res => this.setState({ apiResponse: res }))
      .catch(err => err);
  }

  componentDidMount() {
    this.callAPI();
  }

  handleSwitchDoorsChange(event) {
    this.setState({
      ...this.state,
      switchDoors: !this.state.switchDoors
    })
  }

  render() {
    return (
      <ThemeProvider theme={theme}>
        <div className="App">
          <AppBar position="sticky" color="primary">
            <Toolbar>
              <Typography variant="h6">Monty Hall Simulator</Typography>
            </Toolbar>
          </AppBar>
          <div className="images">
            <img src={monty} alt="Monty Hall"/>
          </div>
          {/* <p>
            {this.state.apiResponse}
          </p> */}
          <Typography>
            Define the number of simulations to run and whether to switch doors after having them opened the first time.
          </Typography>
          <FormGroup row>
            <FormControlLabel
              control={<Checkbox checked={this.state.switchDoors} onChange={this.handleSwitchDoorsChange} name="switchDoorsCheck" />}
              label="Secondary"
            />
          </FormGroup>
          <Button variant="contained" color="secondary">Run simulations</Button>
          <div>Results</div>
          <div>Table with simulations</div>
        </div>
      </ThemeProvider>
    );
  }
}

export default App;
